    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double x0, v0, a, x, v, t;
        DefaultTableModel Table1 = (DefaultTableModel) jTable1.getModel();

        x0 = Double.parseDouble(jTextField1.getText()); // String > double
        v0 = Double.parseDouble(jTextField2.getText());
        a = Double.parseDouble(jTextField3.getText());
        t = Double.parseDouble(jTextField4.getText());

        x = x0 + v0 * t + 0.5 *a * t * t;
        v = v0 + a * t;

        Double[] row = { t, x, v};
        Table1.addRow(row);

        ++t; //t=t+1
        jTextField4.setText(String.valueOf(t)); // double > String
    }//GEN-LAST:event_jButton1ActionPerformed

